package com.java;

public class Ejercicio_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double a = 5.25;
		double b = 6.15;
		double c = 2.1;
				
		double suma = a+b+c;
		double resta = (a-b)-c;
		double multip = (a*b)*c;
		double div = (a/b)/c;
		System.out.println("La suma de las tres variables es: " + suma);
		System.out.println("La resta de las tres variables es: " + resta);
		System.out.println("La multiplicación de las tres variables es: " + multip);
		System.out.println("La división de las tres variables es: " + div);
		
		

	}

}
